mapper.select:
SELECT d.id, d.calle, d.codigoPostal, d.colonia, d.latitud, d.longitud, d.idCiudad 
FROM cliente c, direccion d 
WHERE c.id = #{idCliente} AND c.idDireccion = d.id;

mapper.update:
UPDATE direccion 
SET calle = #{calle}, numero = #{numero}, codigoPostal = #{codigoPostal}, colonia= #{colonia}, latitud = #{latitud}, longitud = #{longitud}, idCiudad = #{idCiudad} 
WHERE id = #{id}
