mapper.select:
SELECT TO_BASE64(logo) AS 'logoBase64' 
FROM empresa 
WHERE id = #{idEmpresa}

mapper.update:
UPDATE empresa 
SET logo = #{logo} 
WHERE id = #{idEmpresa}

mapper.select:
SELECT TO_BASE64(imagen) AS 'imagenBase64' 
FROM promocion 
WHERE id = #{idPromocion}

mapper.update:
UPDATE promocion 
SET imagen = #{imagen} 
WHERE id = #{idPromocion}
